import React, { useState, useEffect } from "react";

import { useHistory } from "react-router-dom";
import UsersList from "./UsersList";
import { Button, Form } from "react-bootstrap";
import "../App.css";

export default function UsersPage() {
  const [error, setError] = useState(null);
  const [loadingUsers, setLoadingUsers] = useState(false);
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage, setUsersPerPage] = useState(2);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    getUsers();
  }, []);

  useEffect(() => {
    getUsers();
  }, [currentPage]);

  function handleUsersPerPageChange(e) {
    setUsersPerPage(e.target.value);
    if (currentPage === 1) {
      return getUsers(e.target.value);
    }
    setCurrentPage(1);
  }
  function goToPrevPage() {
    if (currentPage === 1) {
      return;
    }
    setCurrentPage(currentPage - 1);
  }

  function goToNextPage() {
    if (currentPage === Math.ceil(10 / usersPerPage)) {
      return;
    }
    setCurrentPage(currentPage + 1);
  }

  function getUsers(itemsPerPage = usersPerPage) {
    fetch(
      `https://jsonplaceholder.typicode.com/users?_limit=${itemsPerPage}&_page=${currentPage}`
    )
      .then((res) => res.json())
      .then((results) => {
        setUsers(results);
      })
      .catch((error) => {
        setError(error);
      })
      .finally(() => setLoadingUsers(false));
  }

  function handleDetails(id) {
    const newUsers = users.map((u) => ({ ...u }));
    const index = newUsers.findIndex((user) => user.id === id);
    setCurrentUser(newUsers[index]);
  }

  function handleDelete(id) {
    setUsers(isDeletingUser(id, true));
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`, {
      method: "DELETE",
    })
      .then(() => {
        getUsers();
      })
      .catch((err) => {
        setUsers(isDeletingUser(id, false));
      });
  }

  function isDeletingUser(id, loadingDelete) {
    const newUsers = users.map((p) => ({ ...p }));
    const index = users.findIndex((user) => user.id === id);
    newUsers[index].isLoadingDelete = loadingDelete;
    return newUsers;
  }

  function handleSearchUsers(e) {
    setSearchQuery(e.target.value);
  }

  return (
    <>
      <div className="head-logo">
        <h1>My api calls</h1>
        <Form.Control
          type="text"
          placeholder="Search"
          value={searchQuery}
          onChange={handleSearchUsers}
        />
      </div>
      <br></br>
      <UsersList
        error={error}
        setError={setError}
        loadingUsers={loadingUsers}
        setLoadingUsers={setLoadingUsers}
        users={users}
        setUsers={setUsers}
        getUsers={getUsers}
        handleDelete={handleDelete}
        isDeletingUser={isDeletingUser}
        handleDetails={handleDetails}
      />
      <div class="container">
        <Button
          className="ml-4 "
          onClick={goToPrevPage}
          disabled={currentPage === 1}
        >
          Prev
        </Button>
        <Button
          className="ml-4 "
          onClick={goToNextPage}
          disabled={currentPage === Math.ceil(10 / usersPerPage)}
        >
          Next
        </Button>
        <select className="ml-4 " onChange={handleUsersPerPageChange}>
          <option value={2}>2</option>
          <option value={3}>3</option>
          <option value={4}>4</option>
          <option value={5}>5</option>
        </select>
      </div>
    </>
  );
}
