import React, { useEffect, useState } from "react";
import { Table, Row, Container, Col, Button } from "react-bootstrap";
import "../App.css";
import UserForm from "./UserForm";
import SingleUser from "./SingleUser";
import { useHistory, Link } from "react-router-dom";

function UsersList() {
  const [error, setError] = useState(null);
  const [loadingUsers, setLoadingUsers] = useState(false);
  const [users, setUsers] = useState([]);
  const [loadingDelete, setLoadingDelete] = useState(false);
  const history = useHistory();

  useEffect(() => {
    getUsers();
  }, []);

  function isDeletingUser(id, loadingDelete) {
    const newUsers = users.map((p) => [...p]);
    const index = users.findIndex((user) => user.id === id);
    newUsers[index].isLoadingDelete = loadingDelete;
    return newUsers;
  }

  function handleDelete(id) {
    setUsers(isDeletingUser(id, true));
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`, {
      method: "DELETE",
    })
      .then(() => {
        getUsers();
      })
      .catch((err) => {
        setUsers(isDeletingUser(id, false));
      });
  }

  function getUsers() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((results) => {
        setUsers(results);
      })
      .catch((error) => {
        setError(error);
      })
      .finally(() => setLoadingUsers(false));
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!setLoadingUsers) {
    return <div>Loading</div>;
  } else {
    return (
      <Container>
        <Row className="mt-4 mb-4">
          <Col className="text-center">
            <h1>My api calls</h1>
          </Col>

          <Col className="text-right">
            <Link to="/create">
              <Button>Create new user</Button>
            </Link>
          </Col>
        </Row>
        <Row>
          <Table>
            <thead>
              <tr>
                <th>ID</th>
                <th>First Name</th>
                <th>UserName</th>
                <th>Website</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <SingleUser
                  key={user.id}
                  user={user}
                  isLoadingDelete={user.isLoadingDelete}
                  handleDelete={handleDelete}
                  initialValues={{
                    name: user.name,
                    username: user.username,
                    email: user.email,
                    website: user.website,
                    phone: user.phone,
                  }}
                />
              ))}
            </tbody>
          </Table>
        </Row>
      </Container>
    );
  }
}

export default UsersList;
