import React, { useEffect, useState } from "react";
import { Table, Row, Container, Col, Button } from "react-bootstrap";
import "../App.css";

import { Link } from "react-router-dom";

function UsersList(props) {
  useEffect(() => {
    getUsers();
  }, []);

  function isDeletingUser(id, loadingDelete) {
    const newUsers = props.users.map((p) => [...p]);
    const index = users.findIndex((user) => user.id === id);
    newUsers[index].isLoadingDelete = loadingDelete;
    return newUsers;
  }

  function handleDetails(id) {}
  function handleDelete(id) {
    setUsers(isDeletingUser(id, true));
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`, {
      method: "DELETE",
    })
      .then(() => {
        getUsers();
      })
      .catch((err) => {
        setUsers(isDeletingUser(id, false));
      });
  }

  function getUsers() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((results) => {
        setUsers(results);
      })
      .catch((error) => {
        setError(error);
      })
      .finally(() => setLoadingUsers(false));
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!setLoadingUsers) {
    return <div>Loading</div>;
  } else {
    return (
      <Container>
        <Row className="mt-4 mb-4">
          <Col className="text-center">
            <h1>My api calls</h1>
          </Col>

          <Col className="text-right">
            <Link to="/create">
              <Button>Create new user</Button>
            </Link>
          </Col>
        </Row>
        <Row>
          <Table>
            <thead>
              <tr>
                <th>ID</th>
                <th>First Name</th>
                <th>UserName</th>
                <th>Website</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.username}</td>
                  <td>{user.website}</td>
                  <td>
                    <div>
                      <Button
                        variant="info"
                        onClick={handleDetails}
                        className="mr-2"
                      >
                        Details
                      </Button>
                      <Button
                        variant="danger"
                        onClick={() => handleDelete(user.id)}
                      >
                        {user.isLoadingDelete ? "Loading" : "Delete"}
                      </Button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Row>
      </Container>
    );
  }
}

export default UsersList;
