import React, { useLayoutEffect, useState } from "react";
import { Card, Container, Button } from "react-bootstrap";
import "../UserForm.css";
import { useParams, Link } from "react-router-dom";

export default function SingleUser(props) {
  // Get the id of the specific user
  const { id } = useParams();
  const [user, setUser] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState();

  //Call the API
  useLayoutEffect(() => {
    setLoading(true);
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then((response) => response.json())
      .then((json) => setUser(json))
      .catch((err) => {
        setError(err);
        setLoading(false);
      })
      .finally(() => setLoading(false));
  }, [id]);

  if (error) {
    return <div>{error.message}</div>;
  }
  if (loading) {
    return <div>...Loading...</div>;
  }
  return (
    <Container className="container ">
      <Card className="text-center">
        <Card.Header>I am {user.name}</Card.Header>
        <Card.Body>
          <Card.Title>{user.username}</Card.Title>
          <Card.Text>{user.website}</Card.Text>
          <Card.Text>{user.email}</Card.Text>
          <Card.Footer className="text-muted">{user.phone}</Card.Footer>
        </Card.Body>
      </Card>
      <Link to={`/edit/${user.id}`}>
        <Button variant="info" onClick={props.handleDetails} className="mr-2">
          Details
        </Button>
      </Link>
    </Container>
  );
}
