import React, { useEffect, useState } from "react";

import "./App.css";

import {
  BrowserRouter as Router,
  Route,
  Switch,
  useHistory,
} from "react-router-dom";
import UsersList from "./Components/UsersList";
import UserForm from "./Components/UserForm";
import SingleUser from "./Components/SingleUser";

function App() {
  const history = useHistory();
  const [error, setError] = useState(null);
  const [loadingUsers, setLoadingUsers] = useState(false);
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState();

  useEffect(() => {
    getUsers();
  }, []);

  function getUsers() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((results) => {
        setUsers(results);
      })
      .catch((error) => {
        setError(error);
      })
      .finally(() => setLoadingUsers(false));
  }

  function handleDetails(id) {
    const newUsers = users.map((u) => ({ ...u }));
    const index = newUsers.findIndex((user) => user.id === id);
    setCurrentUser(newUsers[index]);
  }

  function handleDelete(id) {
    setUsers(isDeletingUser(id, true));
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`, {
      method: "DELETE",
    })
      .then(() => {
        getUsers();
      })
      .catch((err) => {
        setUsers(isDeletingUser(id, false));
      });
  }

  function isDeletingUser(id, loadingDelete) {
    const newUsers = users.map((p) => [...p]);
    const index = users.findIndex((user) => user.id === id);
    newUsers[index].isLoadingDelete = loadingDelete;
    return newUsers;
  }

  function handleCreation(data) {
    fetch("https://jsonplaceholder.typicode.com/users", {
      method: "POST",
      body: JSON.stringify({
        name: data.name,
        username: data.username,
        email: data.email,
        website: data.website,
        phone: data.phone,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    });
    history.push("/");
  }
  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <UsersList
            error={error}
            setError={setError}
            loadingUsers={loadingUsers}
            setLoadingUsers={setLoadingUsers}
            users={users}
            setUsers={setUsers}
            getUsers={getUsers}
            handleDelete={handleDelete}
            isDeletingUser={isDeletingUser}
            handleDetails={handleDetails}
          />
        </Route>
        <Route exact path="/create">
          <UserForm
            initialValues={{
              firstName: "",
              username: "",
              email: "",
              website: "",
              phone: "",
            }}
            handleSubmit={handleCreation}
            status="create"
          />
        </Route>
        <Route exact path="/details/:id">
          <SingleUser
            key={currentUser.id}
            user={currentUser}
            isLoadingDelete={currentUser.isLoadingDelete}
            handleDelete={handleDelete}
            initialValues={{
              name: currentUser.name,
              username: currentUser.username,
              email: currentUser.email,
              website: currentUser.website,
              phone: currentUser.phone,
            }}
            status="edit"
          />
          />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
