import React, { useEffect, useState } from "react";
import { Table, Row, Container, Col, Button } from "react-bootstrap";
import "../App.css";

import { Link } from "react-router-dom";

function UsersList(props) {
  const orderArray = [null, "asc", "desc"];
  if (props.error) {
    return <div>Error: {props.error.message}</div>;
  } else if (!props.setLoadingUsers) {
    return <div>Loading</div>;
  } else {
    return (
      <Container>
        <Row className="mt-4 mb-4">
          <Col className="text-right">
            <Link to="/create">
              <Button>Create new user</Button>
            </Link>
          </Col>
        </Row>
        <Row>
          <Table>
            <thead>
              <tr>
                <th>
                  <button>ID</button>
                </th>
                <th>
                  <button>First Name</button>
                </th>
                <th>
                  <button>UserName</button>
                </th>
                <th>
                  <button>Website</button>
                </th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {props.users.map((user) => (
                <tr>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.username}</td>
                  <td>{user.website}</td>
                  <td>
                    <div>
                      <Link to={`/details/${user.id}`}>
                        <Button
                          variant="info"
                          onClick={props.handleDetails}
                          className="mr-2"
                        >
                          Details
                        </Button>
                      </Link>
                      <Button
                        variant="danger"
                        onClick={() => props.handleDelete(user.id)}
                      >
                        {user.isLoadingDelete ? "Loading" : "Delete"}
                      </Button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Row>
      </Container>
    );
  }
}

export default UsersList;
